import { Component } from 'react';
import { Container, Form } from 'react-bootstrap';
import { DoubleArrow } from '@material-ui/icons';
import { TextField, Button } from '@material-ui/core';
import emailjs from 'emailjs-com';
import Footer from '../Footer'  
  class Home extends Component {
      constructor(props) {
            super(props);
            this.state = { 
                rightSide: false,
                formvalues: {
                    name: '',
                    replay_to: '',
                    subject: '',
                    message: ''
                },
                formvalidate:{
                    name: '',
                    replay_to: '',
                    subject: '',
                    message: ''
                }
            };
            this.toggleForm = this.toggleForm.bind(this);
            this.handleOnChange = this.handleOnChange.bind(this);
        }
        toggleForm(){
            this.setState({
                rightSide: !this.state.rightSide
            })
            console.log('toogle');
            document.querySelector('.homebg').classList.toggle('minimize-left')
            document.querySelector('.contactForm-Wrapper').classList.toggle('hide-right')
        }
        handleOnChange(e){
            e.preventDefault();
            console.log(e.target.getAttribute("value"))
            let name = e.target.getAttribute("name");
            let val = e.target.getAttribute("value");
            let tempvalues = this.state.formvalues
            tempvalues[name]=val;
            this.setState({
                formvalues: tempvalues
            })
        }
        sendEmail(e) {
            e.preventDefault();
            console.log(e)

            emailjs.sendForm('service_f57rnog', 'template_36ctgng', e.target, 'user_A8TUKoV7JzmuTJsyAaeac')
              .then((result) => {
                  console.log(result.text);
              }, (error) => {
                  console.log(error.text);
              });
              e.target.reset()
        }
        render() {
        return (
            <>
                <Container fluid className="homebg p-0">
                    <Container className="homeCont"> 
                    {/*<SideNav/>*/}
                        <div className="homeCont_wrapper">
                            <h1>Hey there, I'm Rodrigo.</h1> 
                            <h3>Front End Developer / Web Developer / Self-Taught Dev</h3>
                            <div className="homeCont_descr">
                                <p className="homeCont_descr__highlight">I create engaging and pixel perfect websites.</p>
                                <p className="homeCont_descr__contact">My portfolio is currently under construction ðŸš§<br/>however, don't hesitate to contact me <span className="btnToggForm" onClick={this.toggleForm}>here <DoubleArrow/></span></p>

                            </div>
                        </div>
                    </Container>
                    <Footer/>
                </Container>
                {this.state.rightSide && (
                    <div className="close-right-side" onClick={this.toggleForm}></div>
                )}
                <div className="contactForm-Wrapper hide-right">
                    <div className="contactForm-Inner">
                        <form className='form' noValidate autoComplete="off" onSubmit={this.sendEmail}>
                            <Form.Group>
                                <TextField name="name" label="Name" onChange={this.handleOnChange} variant="filled" value="" required/>
                                <TextField name="replay_to" label="Email" onChange={this.handleOnChange} variant="filled" value="" required/>
                            </Form.Group>
                            <Form.Group>
                                <TextField name="subject" label="Subject" onChange={this.handleOnChange} variant="filled" value="" required/>
                            </Form.Group>
                            <Form.Group>
                                <TextField name="message" multiline rows={8} label="Message" onChange={this.handleOnChange} variant="filled" value="" required/>
                            </Form.Group>
                            <Form.Group>
                                <Button type="submit" variant="contained">Send</Button>
                            </Form.Group>
                        </form>
                        <p>{this.state.formvalues.name}</p>
                        <p>{this.state.formvalues.replay_to}</p>
                        <p>{this.state.formvalues.subject}</p>
                        <p>{this.state.formvalues.message}</p>
                    </div>
                </div>
            </>
            
        )
    }
}
export default Home;
