import { Component } from 'react';
import { Container } from 'react-bootstrap';
import Footer from '../Footer'

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = { rightSide: false };
        this.toggleForm = this.toggleForm.bind(this);
    }
    toggleForm(){
        this.setState({
            rightSide: !this.state.rightSide
        })
        console.log('toogle');
        document.querySelector('.homebg').classList.toggle('minimize-left')
        document.querySelector('.contactFrom-Wrapper').classList.toggle('hide-right')
    }
    render() {
        return (
            <>
                <Container fluid className="homebg p-0">
                    <Container className="homeCont"> 
                    {/*<SideNav/>*/}
                        <div className="homeCont_wrapper">
                            <h1>Hey there, I'm Rodrigo.</h1> 
                            <h3>Front End Developer / Web Developer / Self-Taught Dev</h3>
                            <div className="homeCont_descr">
                                <p className="homeCont_descr__highlight">I create engaging and pixel perfect websites.</p>
                                <p className="homeCont_descr__contact">My portfolio is currently under construction üöß<br/>however, don't hesitate to contact me <span className="btnToggForm" onClick={this.toggleForm}>here ‚ÜóÔ∏è</span></p>

                            </div>
                        </div>
                    </Container>
                    <Footer/>
                </Container>
                {this.state.rightSide && (
                    <div className="close-right-side" onClick={this.toggleForm}></div>
                )}
                <div className="contactFrom-Wrapper hide-right">
                    <div className="contactFrom-Inner">

                    </div>
                </div>
            </>
            
        )
    }
}
export default Home;
